import React, {useState} from 'react';
import axios from 'axios';
import appconfig from "../config/appconfig";
import { useRouter } from 'next/router';
import { message } from "antd";

class GlobalService {
  constructor() {
    this.router = useRouter();
    this.service = axios.create({
      baseURL: appconfig.tmdbEndpoint,
      headers: {
        "Content-Type": "application/json"
      }
    })

    this.service.interceptors.request.use(function(config) {
      return config;
    }, function(error) {
      return Promise.reject(error);
    })

    this.service.interceptors.response.use(function(response) {
      return response;
    }, function(error) {
      this.callErrorHandler(error)
      return Promise.reject(error);
    })
  }

  get(url, config) {
    return new Promise(async (res, rej) => {
      let response = await this.service.get(url, config);
      res(response);
    }).catch((error) => {
      message.error(error?.response?.data?.error);
    });
  }

  post(url, data, config) {
    return new Promise(async (res, rej) => {
      let response = await this.service.post(url, data, config);
      res(response);
    }).catch((error) => {
      message.error(error?.response?.data?.error);
    });
  }

  callErrorHandler(error) {
    switch(error.status) {
      case 401:
        this.handle401(error);
        break;
      default:
        this.handleDefault(error);
        break;
    }
  }

  handle401(error) {
    console.log(error);
    sessionStorage.clear();
    this.router.push({
      pathname: '/error',
      query: {
        error: error
      }
    });
  }

  handleDefault(error) {
    console.log(error)
  }
}

const globalService = new GlobalService();
export default globalService;

