import React, { useState } from 'react';

export default function MovieDetails(props) {
  const { movie } = props;
  const poster =
    "https://image.tmdb.org/t/p/w600_and_h900_bestv2" + movie?.poster_path;
  return (
    <div>
      <img src={poster} alt="poster" />
      <h2>{movie.title}</h2>
    </div>
  )
}
